import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserProfilePage = () => {
  const [user, setUser] = useState(null);
  const [products, setProducts] = useState([]);
  const [exchanges, setExchanges] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      try {
        const userResponse = await axios.get('/api/auth/user', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const productsResponse = await axios.get('/api/products', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const exchangesResponse = await axios.get('/api/exchanges', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setUser(userResponse.data);
        setProducts(productsResponse.data);
        setExchanges(exchangesResponse.data);
        setLoading(false);
      } catch (error) {
        setError(error.response.data.error);
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  if (loading) return <p>Cargando datos...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Perfil de Usuario</h1>
      <h2>Informaci√≥n del Usuario</h2>
      <p>Email: {user.email}</p>
      
      <h2>Productos Publicados</h2>
      <ul>
        {products.map((product) => (
          <li key={product._id}>
            <h3>{product.title}</h3>
            <p>{product.description}</p>
            <div>
              {product.images.map((image, index) => (
                <img key={index} src={image} alt={product.title} width="100" />
              ))}
            </div>
          </li>
        ))}
      </ul>

      <h2>Propuestas de Intercambio</h2>
      <ul>
        {exchanges.map((exchange) => (
          <li key={exchange._id}>
            <p>Producto Ofrecido: {exchange.productOffered.title}</p>
            <p>Producto Solicitado: {exchange.productRequested.title}</p>
            <p>Status: {exchange.status}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserProfilePage;
