import React from 'react';
import { Navbar, Form, FormControl, Button, Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const NavigationBar = ({ isAuthenticated }) => {
  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand as={Link} to="/" className="me-5">
        Truequecito
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Form className="d-flex flex-grow-1">
          <FormControl
            type="search"
            placeholder="Buscar productos..."
            className="me-2"
            aria-label="Search"
          />
          <Button variant="outline-primary">Buscar</Button>
        </Form>
        <Dropdown align="end" className="ms-3">
          <Dropdown.Toggle variant="light" id="dropdown-basic">
            <i className="bi bi-person-fill"></i>
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {isAuthenticated ? (
              <>
                <Dropdown.Item as={Link} to="/perfil">Perfil</Dropdown.Item>
                <Dropdown.Item href="#" onClick={() => {/* Handle logout */}}>Cerrar Sesión</Dropdown.Item>
              </>
            ) : (
              <>
                <Dropdown.Item as={Link} to="/login">Iniciar Sesión</Dropdown.Item>
                <Dropdown.Item as={Link} to="/registro">Registrarse</Dropdown.Item>
              </>
            )}
          </Dropdown.Menu>
        </Dropdown>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default NavigationBar;
